<template>
    <div>
        <div v-for="post in posts" :key="post.id">
            <h2>{{ post.title }}</h2>
            <p>{{ post.description }}</p>
            <select v-model="post.selectedItem" @change="updatePrice(post)">
                <option v-for="item in post.items" :value="item.value">{{ item.name }}</option>
            </select>
            <p>Price: {{ post.price }}</p>
            <p>Likes: {{ post.likes }}</p>
            <button @click="incrementLikes(post)">+1</button>
            <button @click="decrementLikes(post)">-1</button>
            <button @click="deletePost(post)">Delete</button>
        </div>
        <button @click="logData">Log Data</button>
    </div>
</template>

<script>
export default {
    data() {
        return {
            posts: [
                {
                    id: 1,
                    title: "First Post",
                    description: "This is the first post",
                    items: [
                        { name: "Item 1", value: 10 },
                        { name: "Item 2", value: 20 },
                        { name: "Item 3", value: 30 }
                    ],
                    selectedItem: null,
                    price: null,
                    likes: 0
                },
                {
                    id: 2,
                    title: "Second Post",
                    description: "This is the second post",
                    items: [
                        { name: "Item 1", value: 5 },
                        { name: "Item 2", value: 15 },
                        { name: "Item 3", value: 25 }
                    ],
                    selectedItem: null,
                    price: null,
                    likes: 0
                }
            ]
        };
    },
    methods: {
        updatePrice(post) {
            post.price = post.selectedItem;
        },
        incrementLikes(post) {
            post.likes++;
        },
        decrementLikes(post) {
            post.likes--;
        },
        deletePost(post) {
            const index = this.posts.indexOf(post);
            this.posts.splice(index, 1);
        },
        logData() {
            console.log(this.posts);
        }
    }
};
</script>

