<template>
    <div>
        <button @click="addPost">Добавить пост</button>
        <div v-for="(post, index) in posts" :key="index">
            <div>
                <select v-model="post.selectedItem" @change="updatePrice(index)">
                    <option v-for="item in items" :value="item">{{ item.name }}</option>
                </select>
                <span>{{ post.price }}</span>
                <button @click="deletePost(index)">Удалить пост</button>
            </div>
        </div>
    </div>
</template>

<script>
export default {
    data() {
        return {
            posts: [],
            items: [
                { name: "Товар 1", price: 100 },
                { name: "Товар 2", price: 200 },
                { name: "Товар 3", price: 300 },
            ],
        };
    },
    methods: {
        addPost() {
            this.posts.push({ selectedItem: null, price: null });
        },
        deletePost(index) {
            this.posts.splice(index, 1);
        },
        updatePrice(index) {
            const selectedItem = this.posts[index].selectedItem;
            if (selectedItem) {
                this.posts[index].price = selectedItem.price;
            }
        },
    },
};
</script>

